import React, { useEffect, useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import L from 'leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const vehicleIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/854/854894.png',
  iconSize: [32, 32],
  iconAnchor: [16, 16]
});

function App() {
  const [position, setPosition] = useState(null);
  const [path, setPath] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(fetchLocation, 3000);
    } else if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [isRunning]);

  const fetchLocation = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/location');
      const newPos = [res.data.latitude, res.data.longitude];
      setPosition(newPos);
      setPath((prev) => [...prev, newPos]);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ height: '100vh' }}>
      <div style={{ position: 'absolute', zIndex: 999, padding: 10 }}>
        <button onClick={() => setIsRunning(!isRunning)}>
          {isRunning ? 'Pause' : 'Play'}
        </button>
      </div>
      <MapContainer center={[17.385044, 78.486671]} zoom={17} style={{ height: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {position && <Marker position={position} icon={vehicleIcon} />}
        {path.length > 1 && <Polyline positions={path} color="blue" />}
      </MapContainer>
    </div>
  );
}

export default App;
